#pragma config(Motor,	 port2,						rightMotor,		 tmotorVex393, openLoop,		 reversed)
#pragma config(Motor,	 port3,						leftMotor,		 tmotorVex393, openLoop)
#pragma config(Motor,	 port4,						rearRightMotor, tmotorVex393, openLoop,		 reversed)
#pragma config(Motor,	 port5,						rearLeftMotor, tmotorVex393, openLoop,		 reversed)
//*!!Code automatically generated by 'ROBOTC' configuration wizard							 !!*//

